import React, { Component }from 'react';
import axios from 'axios';
import Brand from '../components/Brand';
import "./BrandList.css";

class BrandList extends Component{
  state = {
    isLodaing: true,
    brandList:[]
    }
  // getData= async () => {
  //   try {
  //     const data = await axios.get('dummy.json');
  //     this.setState({ data, isLoading: false });
  //     console.log('dadad' + data);
  //   }
  //   catch (error) {
  //     console.log(error);
  //   }
  // }
  componentDidMount() {
    // this.getData();
    fetch('dummy.json')
      .then(function (result) {
        return result.json();
      })
      .then(function (result) {
        console.log("result :", result);
        console.log(result[0].id);
        this.setState({ isLoading: false, brandList:result});
      }.bind(this));
  }
  render() {
    const { isLoading, brandList } = this.state;
    return (
      <section className="container">
      {isLoading ? (
        <div className="loader">
          <span className="loader__text">Loading...</span>
        </div>
      ) : (
        <div className="brand__list">
              <span>브랜드 소개하는 페이지</span>
              {brandList.map(brandList => (
                <Brand
                key={brandList.id}
                id={brandList.id}
                name={brandList.name}
                desc={brandList.desc}
                homepage={brandList.homepage}
                image={brandList.image}
                />
              ))}
        </div>
        )}
    </section>
    );
  }
} 

export default BrandList;
