import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import './Brand.css';


function Brand({ id,name,desc,homepage, image}) {
  return (
    <Link to={{
      pathname: `/movie/${id}`,
      state: {
        year,
        title,
        summary,
        poster,
        genres
      }
      }}>
      <div className="brand">
          <img src={poster} alt={title} title={title}/>
          <div className="brand__data">
            <h3 className="brand__title">{title}</h3>
            <h5 className="brand__year">{year}</h5>
                <p className="brand__summary">{summary.slice(0,180)}...</p>
                <ul className="genres">{genres.map((genre, index) => <li key={index} className="genres__genre">{genre}</li>)}</ul>
        </div>
      </div>
      </Link>
    );
}

Brand.propTypes = {
    id: PropTypes.number.isRequired,
    name: PropTypes.number.isRequired,
    desc: PropTypes.string.isRequired,
    hompage: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
};

export default Brand;
